*,
*::before,
*::after {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    font-size: 62.5%;
}

body {
    font-size: 1.6rem;
    font-family: $font-stack;
    line-height: 1.5;
    background-color: $bg-color;
    color: $text-color;
}

h1,h2,h3,h4 {
    line-height: 1;
}

a {
    text-decoration: none;
}

ul {
    list-style: none;
}

img {
    max-width: 100%;
}

.container {
    width: 100%;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
    overflow: hidden;
}


.section {
    width: 100%;
    padding: 2rem 0;
}

// Text 
.xlarge {
    font-size: 4.5rem;
    letter-spacing: 3px;
    margin-bottom: 2rem;
}

.lines-bottom {
    position: relative;
    margin-bottom: 3.5rem;

    &::before,
    &::after {
        content: '';
        width: 6rem;
        height: 2px;
        background-color: $primary-color;
        position: absolute;
        left: 0;    
    }

    &::before {
        bottom: -1rem;
    }
    &::after {
        bottom: -2rem;
        left: 3rem;
    }
}

@include mq(768px) {
    .container {
        padding: 0 3rem;
    }
    .xlarge {
        font-size: 5rem;
    }
    .lines-bottom {
        margin-bottom: 6rem;
        &::before,
        &::after {
            width: 10rem;
            height: 4px;
        }
        &::before {
            bottom: -2rem;
        }
        &::after {
            bottom: -4rem;
        }
    }
}

