.global-nav {
	width: 100%;
	height: 100vh;
	background-image: linear-gradient(to right, $bg-color, $secondary-color 42%, $primary-color);
	padding: 4rem 2rem;
	@include flex;
	text-align: center;
	position: absolute;
	top: 0;
	left: 0;
	transform: translateY(-100%);
	z-index: -1;

	li:not(:last-child) {
		margin-bottom: 1rem;
	}

	&__sns {
		a {
			font-size: 2rem !important;
		}
		@include mq(768px) {
			display: none;
		}
	}

	a {
		display: inline-block;
		padding: .5rem;
		font-size: 4.4rem;
		font-weight: 600;
		letter-spacing: 2px;
		text-transform: uppercase;
		color: $text-color;
		transform-origin: center bottom;
		//font-family: $font-inknut;

		@include mq(768px) {
			font-size: 6.6rem;
		}
	}
}

