.hero {
    position: relative;
    width: 100%;
    height: 100vh;
    overflow: hidden;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-end;

    @include mq(768px) {
        justify-content: center;
    }

    &__text {
        padding-bottom: 10rem;
        font-family: $font-inknut;
        @include mq(768px) {
            line-height: 1.8;
            width: 40rem;
            padding-bottom: 0;

            p {
                font-size: 2.2rem;
            }
        }
    }
}

.moon {
    width: 165vw;
    height: 100vh;
    position: absolute;
    left: 50%;
    top: -10rem;
    transform: translateX(-50%);
    display: flex;
    align-items: center;
    justify-content: center;

    @include mq(768px) {
        top: 0;
        width: 100%;
        left: 30%;
        transform: translateX(0);
    }

    

    &__text-wrapper {
        position: absolute;
        top: 45%;
        left: 3rem;
        z-index: 110 !important;
        color: $primary-color;

        @include mq(768px) {
            top: 48%;
            left: 25%;
        }
        
        h1 {
            font-size: 3rem;
            text-transform: uppercase;
            letter-spacing: 2px;
            text-shadow: 2px 5px 5px rgba(0,0,0, 0.4);
            @include mq(768px) {
                font-size: 4rem;
            }
        }
    }

    &light {
        width: 100%;
        height: 100vh;
        position: absolute;
        
        &__img {
            width: 100%;
            height: 100%;
            background-image: url('../../public/images/hero/moonlight.svg');
            opacity: 0.8;
            background-repeat: no-repeat;
            background-size: 100%;
            background-position: center;
        }
    }

    &__wrapper {
        width: 100vw;
        height: 100%;
        position: absolute;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    &__img {
        width: 25rem;
        position: absolute;
        z-index: 90;
        @include mq(768px) {
            width: 30rem;
        }
    }

    .layer {
        width: 20rem;
        height: auto;
        position: absolute;

        @include mq(768px) {
            width: 25rem;
        }

        img {
            width: 100%;
            height: auto;
        }

        &--1 {
            top: 38%;
            right: -4rem;
            @include mq(768px) {
                top: 40%;
                right: 20%;
            }
        }
        &--2 {
            top: 58%;
            right: 0;
            @include mq(768px) {
                top: 55%;
                right: 25%;
            }
        }
        &--3 {
            top: 50%;
            left: -4rem;
            @include mq(768px) {
                top: 52%;
                left: 4rem;
            }
        }
        &--4 {
            top: 35%;
            left: -10%;
            @include mq(768px) {
                left: 12rem;
            }
        }
        &--5 {
            top: 25%;
            left: 30%;
            @include mq(768px) {
                left: 40%;
                top: 28%;
            }
        }
    }

    &__front {
        z-index: 100;
    }

    &__back {
        z-index: 10;
    }
}

