.header {
    width: 100%;
    height: 60px;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 900;
    @include mq(768px) {
        height: 80px;
    }

    .wrap {
        @include flex;
    }
    .icons {
        display: none;
        font-size: 3rem;
        margin-right: 2rem;

        a {
            color: $text-color;
            padding: 0.25rem 1rem;
            &:not(:last-child) {
                margin-right: 1rem;
            }
        }

        @include mq(768px) {
            display: flex;
        }
    }

    &__container {
        height: 100%;
        @include flex(center, space-between);
    }

    .logo {
        font-size: 2rem;
        color: $text-color;
        letter-spacing: 3px;
        @include mq(768px) {
            font-size: 3rem;
        }
    }
}

.menu-icon {
    width: 30px;
    height: 30px;
    @include flex;
    position: relative;
    cursor: pointer;

    @include mq(768px) {
        width: 50px;
        height: 50px;
    }

    &.open {
        &::before,
        &::after {
            top: 50%;
            transition: top $t-time $t-ease, transform $t-time $t-ease $t-time;
        }

        &::before {
            transform: rotate(45deg);
        }
        &::after {
            transform: rotate(-45deg);
        }
    }

    &::before,
    &::after {
        content: '';
        width: 90%;
        height: 3px;
        background-color: $text-color;
        position: absolute;
        transition: top $t-time $t-ease $t-time, transform $t-time $t-ease;
    }

    &::before {
        top: 25%;
    }

    &::after {
        top: 60%;
    }
}

.global-nav {
	width: 100%;
	height: 100vh;
	background-image: linear-gradient(to right, $bg-color, $secondary-color 42%, $primary-color);
	padding: 4rem 2rem;
	@include flex;
	text-align: center;
	position: absolute;
	top: 0;
	left: 0;
	transform: translateY(-100%);
	z-index: -1;

	li:not(:last-child) {
		margin-bottom: 1rem;
	}

	&__sns {
		a {
			font-size: 2rem !important;
		}
		@include mq(768px) {
			display: none;
		}
	}

	a {
		display: inline-block;
		padding: .5rem;
		font-size: 4.4rem;
		font-weight: 600;
		letter-spacing: 2px;
		text-transform: uppercase;
		color: $text-color;
		transform-origin: center bottom;
		//font-family: $font-inknut;

		@include mq(768px) {
			font-size: 6.6rem;
			position: relative;

			&::before {
				content: '';
				width: 100%;
				height: 6px;
				background-color: $text-color;
				position: absolute;
				top: 50%;
				left: 0;
				transform: translateY(-50%) scaleX(0);
				transform-origin: left center;
				transition: transform $t-time $t-ease;
			}

			&:hover::before {
				transform: translateY(-50%) scaleX(1);
			}
		}
	}
}

